#for each category, one hot encoding for each subj pair, 1 if in same cluster, 0 if not
def get_vectors():
    #dictionary with tuple (subj1,subj2) keys and one hot encoding val ([1,0,0,1...] len num cats)
    subj_dict = {}
    subjects = set()
    #get all subjects
    for catLists in clusters.values():
        for l in catLists:
            for s in l:
                subjects.add(s)
    #subject pairs as keys in dict
    subject_pairs = []
    for s1 in subjects:
        for s2 in subjects:
            if s1 != s2:
                if (tuple(sorted([s1,s2])) not in subject_pairs):
                    subject_pairs.append(tuple(sorted([s1,s2])))
    vectors = {}
    for cat, lists in clusters.items():
        vectors[cat] = []
        for (s1,s2) in subject_pairs:
            ok=False
            for i in range(3):
                if s1 in lists[i] and s2 in lists[i]:
                    ok=True
            if(ok):
                vectors[cat].append(1)
            else:
                vectors[cat].append(0)   
    return vectors

vectors = get_vectors()

#for each pair of categories, correlation of each pair of subjects being clustered together or not
def get_correlations(vecs):
    corrs = {}
    for cat1, vec1 in vecs.items():
        for cat2, vec2 in vecs.items():
            if (cat1 != cat2) and (tuple(sorted([cat1,cat2])) not in corrs.keys()):
                c = np.corrcoef(vec1, vec2)[0][1]
                corrs[tuple(sorted([cat1,cat2]))] = c
    return corrs

corrs = get_correlations(vectors)
